const log = require('logToConsole');
const sendPixel = require('sendPixel');
const getCookieValues = require('getCookieValues');
const getUrl = require('getUrl');
const getReferrerUrl = require('getReferrerUrl');
const getTimestampMillis = require('getTimestampMillis');
const generateRandom = require('generateRandom');
const JSON = require('JSON');
const encodeUriComponent = require('encodeUriComponent');
const copyFromDataLayer = require('copyFromDataLayer');

// Event mapping from GA4 to Meta format
const eventMap = {
  'page_view': 'PageView',
  'view_item': 'ViewContent',
  'add_to_cart': 'AddToCart',
  'remove_from_cart': 'RemoveFromCart',
  'view_cart': 'ViewCart',
  'begin_checkout': 'InitiateCheckout',
  'add_payment_info': 'AddPaymentInfo',
  'add_shipping_info': 'AddShippingInfo',
  'purchase': 'Purchase',
  'generate_lead': 'Lead',
  'sign_up': 'CompleteRegistration',
  'search': 'Search',
  'submit_purchase': 'Purchase'
};

// Helper function to check valid values
function isValidValue(value) {
  if (value === null || value === undefined) return false;
  if (typeof value === 'string') {
    if (value === '' || value === 'undefined' || value === 'null') return false;
  }
  return true;
}

// GTM internal events that should be ignored
const gtmInternalEvents = [
  'gtm.js',
  'gtm.dom',
  'gtm.load',
  'gtm.click',
  'gtm.linkClick',
  'gtm.formSubmit',
  'gtm.historyChange',
  'gtm.scrollDepth',
  'gtm.timer',
  'gtm.video',
  'gtm.init',
  'gtm.init_consent'
];

// Check if event is a GTM internal event
function isGtmInternalEvent(eventName) {
  if (!eventName) return false;
  for (var i = 0; i < gtmInternalEvents.length; i++) {
    if (eventName === gtmInternalEvents[i]) {
      return true;
    }
  }
  return false;
}

// Get event name
function getEventName() {
  if (data.eventSource === 'manual') {
    if (data.eventName === 'CustomEvent') {
      return data.customEventName || 'CustomEvent';
    }
    return data.eventName;
  }

  // Auto-detect from dataLayer
  const dlEvent = copyFromDataLayer('event');

  // Skip GTM internal events
  if (isGtmInternalEvent(dlEvent)) {
    return null; // Signal to skip this event
  }

  if (dlEvent && eventMap[dlEvent]) {
    return eventMap[dlEvent];
  }
  return dlEvent || 'PageView';
}

// Get Facebook cookies
function getFbCookies() {
  let fbp = null;
  let fbc = null;

  if (data.autoCaptureFbCookies) {
    const fbpCookies = getCookieValues('_fbp');
    const fbcCookies = getCookieValues('_fbc');

    if (fbpCookies && fbpCookies.length > 0) {
      fbp = fbpCookies[0];
    }
    if (fbcCookies && fbcCookies.length > 0) {
      fbc = fbcCookies[0];
    }
  }

  // Override with variables if provided
  if (isValidValue(data.fbpVariable)) {
    fbp = data.fbpVariable;
  }
  if (isValidValue(data.fbcVariable)) {
    fbc = data.fbcVariable;
  }

  return { fbp: fbp, fbc: fbc };
}

// Generate event ID
function getEventId() {
  if (isValidValue(data.eventIdVariable)) {
    return data.eventIdVariable;
  }

  if (data.autoGenerateEventId) {
    const timestamp = getTimestampMillis();
    const random = generateRandom(100000000, 999999999);
    return timestamp + '_' + random;
  }

  return null;
}

// Build user data object
function buildUserData() {
  const userData = {};
  const fbCookies = getFbCookies();

  if (isValidValue(data.userEmail)) userData.em = data.userEmail;
  if (isValidValue(data.userPhone)) userData.ph = data.userPhone;
  if (isValidValue(data.userFirstName)) userData.fn = data.userFirstName;
  if (isValidValue(data.userLastName)) userData.ln = data.userLastName;
  if (isValidValue(data.userCity)) userData.ct = data.userCity;
  if (isValidValue(data.userCountry)) userData.country = data.userCountry;
  if (isValidValue(data.userZip)) userData.zp = data.userZip;
  if (isValidValue(data.externalId)) userData.external_id = data.externalId;
  if (isValidValue(fbCookies.fbp)) userData.fbp = fbCookies.fbp;
  if (isValidValue(fbCookies.fbc)) userData.fbc = fbCookies.fbc;
  if (isValidValue(data.clientIdVariable)) userData.client_id = data.clientIdVariable;
  if (isValidValue(data.sessionIdVariable)) userData.session_id = data.sessionIdVariable;

  return userData;
}

// Build custom data object
function buildCustomData() {
  const customData = {
    url: getUrl(),
    referrer: getReferrerUrl()
  };

  if (isValidValue(data.contentName)) {
    customData.content_name = data.contentName;
  }
  if (isValidValue(data.contentCategory)) {
    customData.content_category = data.contentCategory;
  }
  if (isValidValue(data.transactionId)) {
    customData.order_id = data.transactionId;
  }
  if (isValidValue(data.searchQuery)) {
    customData.search_string = data.searchQuery;
  }

  // Handle items array
  if (isValidValue(data.eventItems)) {
    const items = data.eventItems;

    if (typeof items === 'object') {
      const itemsArray = items.length ? items : [items];

      if (itemsArray.length > 0) {
        customData.content_ids = [];
        customData.contents = [];
        customData.num_items = 0;
        customData.content_type = 'product';

        for (let i = 0; i < itemsArray.length; i++) {
          const item = itemsArray[i];
          const itemId = item.item_id || item.id || '';

          if (itemId) {
            customData.content_ids.push(itemId + '');
            customData.contents.push({
              id: itemId + '',
              quantity: item.quantity || 1,
              item_price: item.price || 0
            });
            customData.num_items += (item.quantity || 1);
          }
        }
      }
    }
  }

  return customData;
}

// Build the payload
const eventName = getEventName();

// Skip GTM internal events (null event name)
if (eventName === null) {
  log('PixelFly: Skipping GTM internal event');
  data.gtmOnSuccess();
  return;
}

const eventId = getEventId();

const payload = {
  event: eventName,
  value: isValidValue(data.eventValue) ? data.eventValue * 1 : 0,
  currency: isValidValue(data.eventCurrency) ? data.eventCurrency : 'BDT',
  user_data: buildUserData(),
  custom_data: buildCustomData()
};

if (eventId) {
  payload.event_id = eventId;
}

// Add transaction details
if (isValidValue(data.transactionId)) {
  payload.transaction_id = data.transactionId;
}
if (isValidValue(data.eventTax)) {
  payload.tax = data.eventTax * 1;
}
if (isValidValue(data.eventShipping)) {
  payload.shipping = data.eventShipping * 1;
}
if (isValidValue(data.eventCoupon)) {
  payload.coupon = data.eventCoupon;
}

// Encode payload as base64-like URL-safe string
const payloadJson = JSON.stringify(payload);
const encodedPayload = encodeUriComponent(payloadJson);

// Build the tracking URL with payload as query parameter
// The endpoint will accept both GET (with ?d=payload) and POST
const trackingUrl = data.endpoint + '?k=' + encodeUriComponent(data.apiKey) + '&d=' + encodedPayload;

// Send the pixel request
sendPixel(trackingUrl, data.gtmOnSuccess, data.gtmOnFailure);


